mapper.update:
UPDATE board
SET title = #{title}, content = #{content}, img_path = #{img_path}, praise_target = #{praise_target}, is_public = #{is_public}, up_id = #{writer}, up_date = NOW()
WHERE bno = #{bno}

UPDATE board
SET view_cnt = #{view_cnt}
WHERE bno = #{bno}

UPDATE board
SET del_status = 'Y', del_date = NOW()
WHERE bno = #{bno}
